import React from 'react';
import ReactDOM from 'react-dom';
import { createStore } from 'redux'
import { noteReducer } from './note-reducer/noteReducer';



const store = createStore(noteReducer);

store.dispatch({
  type: '@notes/created',
  payload: {
    content: 'Me encanta programar Redux',
    important: true,
    id: 1
  }
})

store.dispatch({
  type: '@notes/created',
  payload: {
    content: 'pero es aburrido',
    important: false,
    id: 2
  }
})


const App = () => {
  const state = store.getState();

  return(
    <ul>
      {
      state.map(note => {
        return <li key={note.id}>
          {note.content}
          <strong>
            {
            note.important 
              ? 'important' 
              : 'not important'

            }
          </strong>
        </li>
        })
      }
    </ul>
  )
}

const renderApp = () => {
  ReactDOM.render(
    <App />,
  document.getElementById('root')
  );
}

renderApp()
store.subscribe(renderApp);
