export default function NewNote() {
    const addNote = (event) => {
        event.preventDefault()
        const {target} = event
        const content = target.note.value
        target.note.value = ''
        dispatch(createNote(content))
      }
    return (
        <form onSubmit={addNote}>
            <input name="note" />
            <button>Add</button>
        </form>
    )
}
