const initialState = [
  {
    content: 'primera nota para las pruebas',
    important: true,
    id: 1
  },{
    content: 'segunda nota para las pruebas',
    important: false,
    id: 2
  },
]

export const noteReducer = (state = initialState, action) => { 
    if (action.type === '@notes/created'){
      return [...state, action.payload];
    }

    if(action.type === '@notes/toggle_important') {
        const {id} = action.payload
        return state.map (note => {
            if(note.id === id) {
                return {
                    ...note,
                    important: !note.important
                }
            }
            return note;
        })
    }

    return state;

  }

const generateId = () => 
  Math.floor(Math.random() * 9999999) + 1


export const createNote = content => {
    return {
      type: '@notes/created',
