import React from 'react';
import ReactDOM from 'react-dom';
import { createStore } from 'redux'


const noteReducer = (state = [], action) => { 
  if (action.type === 'NEWNOTE'){
    state.push(action.payload)
    return state;
  }

  return state;

}
